import React, { Component } from 'react';
import './App.css';
import firebase, { auth, provider } from './firebase.js';

export default class HomePage extends Component {

    handleSubmit(e) {
        e.preventDefault();
        const itemsRef = firebase.database().ref('items');

        const item = {
          title: this.props.state.currentItem,
          user: this.props.state.user.displayName || this.props.state.user.email
        }

        itemsRef.push(item);
        this.setState({
          currentItem: '',
          username: ''
        });
    }

    handleChange(e) {
        this.setState({
          [e.target.name]: e.target.value
        });
    }


    render() {
        return (
            <div>
            <div className='user-profile'>
              <img src={this.props.state.user.photoURL} />
            </div>
            <div className='container'>
              <section className='add-item'>
                <form onSubmit={this.handleSubmit}>
                  <input type="text" name="username" placeholder="What's your name?" value={this.props.state.user.displayName || this.props.state.user.email} />
                  <input type="text" name="currentItem" placeholder="What are you bringing?" onChange={this.handleChange} value={this.props.state.currentItem} />
                  <button>Add Item</button>
                </form>
              </section>
              <section className='display-item'>
                <div className="wrapper">
                  <ul>
                    {this.props.state.items.map((item) => {
                      return (
                        <li key={item.id}>
                          <h3>{item.title}</h3>
                          <p>posted by: {item.user}
                            {item.user === this.props.state.user.displayName || item.user === this.props.state.user.email ?
                              <button onClick={() => this.removeItem(item.id)}>Remove Item</button> : null}
                          </p>
                        </li>
                      )
                    })}
                  </ul>
                </div>
              </section>
            </div>
          </div>
        )
    }
}
