import React, { Component } from 'react';
import './App.css';
import firebase, { auth, provider } from './firebase.js';


class App extends Component {
  constructor() {
    super();
    this.state = {
      currentItem: '',
      username: '',
      items: [],
      user: null,
      homepage: false,
      groups: [
        {
            id: '1',
            name: 'men',
            members: ['ken', 'john', 'ruth brennan'],
            admin: 'ken'
        },
        {
          id: '2',
          name: 'women',
          members: ['ruth brennan', 'mary', 'sophie'],
          admin: 'ruth brennan'
        }
      ],
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.login = this.login.bind(this); 
    this.logout = this.logout.bind(this); 
  }

  login() {
    auth.signInWithPopup(provider) 
      .then((result) => {
        const user = result.user;
        this.setState({
          user
        });
      });
  }

  logout() {
    auth.signOut()
      .then(() => {
        this.setState({
          user: null
        });
      });
  }

  handleSubmit(e) {
      e.preventDefault();
      const itemsRef = firebase.database().ref('items');

      const item = {
        title: this.state.currentItem,
        user: this.state.user.email
      }

      itemsRef.push(item);
      this.setState({
        currentItem: '',
        username: ''
      });
  }

  handleAddGroup(e) {
    //TODO
  }

  handleChange(e) {
      this.setState({
        [e.target.name]: e.target.value
      });
  }


  componentDidMount() {
    //Login stuff
    auth.onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } 
    });
    //Getting items from db
    const itemsRef = firebase.database().ref('items');
    itemsRef.on('value', (snapshot) => {
      let items = snapshot.val();
      let newState = [];
      for (let item in items) {
        newState.push({
          id: item,
          title: items[item].title,
          user: items[item].user
        });
      }
      this.setState({
        items: newState
      });
    });
    //Getting Groups from db

  }

  removeItem(itemId) {
    const itemRef = firebase.database().ref(`/items/${itemId}`);
    itemRef.remove();
  }

  render() {
    return (
      <div className='app'>
        <header>
          <div className="wrapper">
            <h1>Social App</h1>
            {this.state.user ?
              <button onClick={this.logout}>Logout</button>                
              :
              <button onClick={this.login}>Log In</button>              
            } 
          </div>
        </header>

        {this.state.user ?
          <div>
            { this.state.homepage ?
              //We are on the homepage
              <div>
                <div className='user-profile'>
                  <img src={this.state.user.photoURL} />
                </div>
                <div className='container'>
                  <section className='add-item'>
                    <form onSubmit={this.handleSubmit}>
                      <input type="text" name="username" placeholder="What's your name?" value={this.state.user.displayName || this.state.user.email} />
                      <input type="text" name="currentItem" placeholder="What are you bringing?" onChange={this.handleChange} value={this.state.currentItem} />
                      <button>Add Item</button>
                    </form>
                  </section>
                  <section className='display-item'>
                    <div className="wrapper">
                      <ul>
                        {this.state.items.map((item) => {
                          return (
                            <li key={item.id}>
                              <h3>{item.title}</h3>
                              <p>posted by: {item.user}
                                {item.user === this.state.user.displayName || item.user === this.state.user.email ?
                                  <button onClick={() => this.removeItem(item.id)}>Remove Item</button> : null}
                              </p>
                            </li>
                          )
                        })}
                      </ul>
                    </div>
                  </section>
                </div>
              </div>
              :
              //We are on the group page
              <div>
                <div className='user-profile'>
                  <img src={this.state.user.photoURL} />
                </div>
                <div className='container'>
                  <section className='add-item'>
                    <form onSubmit={this.handleAddGroup}>
                      <input type="text" name="username" placeholder="What's your name?" onChange={this.handleChange} value={this.state.currentItem} />
                      <button>Add Item</button>
                    </form>
                  </section>
                  <section className='display-item'>
                    <div className="wrapper">
                      <ul>
                        {this.state.items.map((item) => {
                          return (
                            <li key={item.id}>
                              <h3>{item.title}</h3>
                              <p>posted by: {item.user}
                                {item.user === this.state.user.displayName || item.user === this.state.user.email ?
                                  <button onClick={() => this.removeItem(item.id)}>Remove Item</button> : null}
                              </p>
                            </li>
                          )
                        })}
                      </ul>
                    </div>
                  </section>
                </div>
              </div>
            }
          </div>
          :
          <div className='wrapper'>
            <p>You must be logged in.</p>
          </div>
        }

      </div>
    );
  }
}
export default App;
