import React, { Component } from 'react';
import './App.css';
import firebase, { auth, provider } from './firebase.js';
var Crypto = require("crypto");
var CryptoJS = require("crypto-js");


class App extends Component {
  constructor() {
    super();
    this.state = {
      currentItem: '',
      username: '',
      user: null,
      items:[],
      homepage: true,
      groupName: '',
      memberToGroup: '',
      memberName: '',
      groups: [],
      postToGroup: '',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.login = this.login.bind(this); 
    this.logout = this.logout.bind(this); 
    this.handleAddGroup = this.handleAddGroup.bind(this);
    this.handleAddToGroup = this.handleAddToGroup.bind(this);
    this.switchPage = this.switchPage.bind(this);
    this.text = this.text.bind(this);
    this.decrypt = this.decrypt.bind(this);
  }

  login() {
    auth.signInWithPopup(provider) 
      .then((result) => {
        const user = result.user;
        this.setState({
          user
        });
      });
  }

  logout() {
    auth.signOut()
      .then(() => {
        this.setState({
          user: null
        });
      });
  }

  handleSubmit(e) {
      e.preventDefault();
      const itemsRef = firebase.database().ref('items');

      // Generate random hash using date and random string
      var current_date = (new Date()).valueOf().toString();
      var random = Math.random().toString();
      var key = Crypto.createHash('sha1').update(current_date + random).digest('hex').toString();
      console.log('key');
      console.log(key);


      // Encrypt file data using key
      var encryptedPost = CryptoJS.AES.encrypt(this.state.currentItem, key).toString();
      console.log('encrypt: ');
      console.log(encryptedPost);

      const item = {
        title: encryptedPost,
        group: this.state.postToGroup,
        user : this.state.user.displayName,
        key: key,
      }

      itemsRef.push(item);
      this.setState({
        currentItem: '',
        postToGroup: '',
      });
  }

  decrypt(cipherText, key) {
    var originalText = CryptoJS.AES.decrypt(cipherText, key).toString(CryptoJS.enc.Utf8);
    return originalText;
  }

  text(item) {

    console.log('item = ' + item);
    //Getting Groups from db
    let decrypt = false;
    const groupRef = firebase.database().ref('groups');
    groupRef.on('value', (snapshot) => {
      let tempGroups = snapshot.val();
      for (let group in tempGroups) {
        if (tempGroups[group].name == item.group) {
          if (tempGroups[group].members.includes(this.state.user.displayName)) {
            decrypt=true;
          } 
        }
      }
    });

    if (decrypt) {
      console.log( 'key to decrypt ' + item.key);
      console.log( 'key to decrypt group' + item.key);
      var hold = this.decrypt(item.title,item.key);
      return (hold);
    } else {
      return(item.title);
    }


  }

  handleAddGroup(e) {
    e.preventDefault();
    const groupsRef = firebase.database().ref('groups');

    var memberList = [this.state.user.displayName];

    const group = {
      name: this.state.groupName,
      members: memberList,
      admin: this.state.user.displayName
    }

    groupsRef.push(group);
    this.setState({
      groupName: '',
    });

  }

  handleAddToGroup(e) {

    e.preventDefault();

    let newGroups = '';
    //Getting Groups from db
    const groupRef = firebase.database().ref('groups');
    groupRef.on('value', (snapshot) => {
      newGroups = snapshot.val();
      for (let group in newGroups) {
        console.log(newGroups[group].name);
        if (newGroups[group].name == this.state.memberToGroup) {
          console.log('found wow');
          console.log(newGroups[group].name);
          newGroups[group].members.push(this.state.memberName);
        }
      }
    });

    //console.log(newGroups);
    groupRef.set(newGroups);
    this.setState({
      memberToGroup: '',
      memberName: '',
    });

  }

  handleChange(e) {
      this.setState({
        [e.target.name]: e.target.value
      });
  }


  componentDidMount() {
    //Login stuff
    auth.onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } 
    });
    //Getting items from db
    const itemsRef = firebase.database().ref('items');
    itemsRef.on('value', (snapshot) => {
      let items = snapshot.val();
      let newState = [];
      for (let item in items) {
        newState.push({
          id: item,
          title: items[item].title,
          user: items[item].user,
          group: items[item].group,
        });
      }
      this.setState({
        items: newState
      });
    });

    //Getting Groups from db
    const groupRef = firebase.database().ref('groups');
    groupRef.on('value', (snapshot) => {
      let groups = snapshot.val();
      let newState = [];
      for (let group in groups) {
        newState.push({
          id: group,
          name: groups[group].name,
          members: groups[group].members,
          admin: groups[group].admin
        });
      }
      this.setState({
        groups: newState
      });
    });

  }

